import { rgba } from 'polished'
import { BrushContext, IBrush } from '../engine/IBrush'
import * as THREE from 'three'
import {
  Matrix4,
  PlaneBufferGeometry,
  SphereBufferGeometry,
  SphereGeometry,
  Vector2,
} from 'three'

const DOGE =
  'data:image/jpeg;base64,'

const _matrix = new Matrix4()
const centerAnchor = new Vector2(0.8, 0.5)

export class ScatterBrush implements IBrush {
  public static readonly id = '@silk-paint/scatter-brush'

  public get id() {
    return ScatterBrush.id
  }

  private scene: THREE.Scene
  private material: THREE.SpriteMaterial
  private sprite: THREE.Sprite
  private texture: THREE.Texture
  private path: THREE.Path

  public async initialize() {
    this.scene = new THREE.Scene()
    this.path = new THREE.Path()

    const texture = (this.texture = await new Promise<THREE.Texture>((r) =>
      new THREE.TextureLoader().load(DOGE, r)
    ))

    this.material = new THREE.SpriteMaterial({ map: texture, color: 0xffffff })

    const width = texture.image.width,
      height = texture.image.height

    this.sprite = new THREE.Sprite(this.material)
    this.sprite.position.set(0, 0, 0)
    this.sprite.scale.set(width, -height, 1)

    // this.scene.add(this.sprite)
    // console.log('init', this.sprite.scale, DOGE)
  }

  public render({
    context: ctx,
    threeRenderer: renderer,
    threeCamera: camera,
    stroke,
    ink,
    brushSetting: { weight, color, opacity },
    size,
  }: BrushContext) {
    const path = new THREE.Path()

    const [start] = stroke.path.points

    path.moveTo(start.x, start.y)
    stroke.path.mapPoints(
      (point, prev) => {
        // console.log({ point })
        path.bezierCurveTo(
          prev?.out?.x ?? prev!.x / size.width,
          prev?.out?.y ?? prev!.y / size.height,
          point.in?.x ?? point.x / size.width,
          point.in?.y ?? point.y / size.height,
          point.x / size.width,
          point.y / size.height
        )
      },
      { startOffset: 1 }
    )

    // const onepixScaleW =
    const points = path.getPoints()
    const geometry = new THREE.InstancedBufferGeometry().setFromPoints(points)
    const sphere = new PlaneBufferGeometry(20, 20)
    const mesh = new THREE.InstancedMesh(
      sphere,
      this.material,
      // new THREE.MeshBasicMaterial({ color: 0xff0000 }),
      points.length
    )
    ;(mesh as any).center = centerAnchor
    mesh.instanceMatrix.setUsage(THREE.StaticDrawUsage)

    points.forEach((p, i) => {
      //   console.log({ ...p })
      _matrix.setPosition(Math.random(), Math.random(), 0)
      mesh.setMatrixAt(i, _matrix)
    })
    console.log({ points })

    mesh.setMatrixAt(0, new Matrix4().setPosition(0, 0, 0))
    mesh.setMatrixAt(1, new Matrix4().setPosition(size.width, 0, 0))
    mesh.setMatrixAt(2, new Matrix4().setPosition(0, size.height, 0))
    mesh.setMatrixAt(3, new Matrix4().setPosition(size.width, size.height, 0))

    mesh.setMatrixAt(4, new Matrix4().setPosition(-0.5, 0, 0))
    mesh.setMatrixAt(5, new Matrix4().setPosition(0.5, 0, 0))
    mesh.setMatrixAt(6, new Matrix4().setPosition(0, 0.5, 0))
    mesh.setMatrixAt(7, new Matrix4().setPosition(0.5, 0.5, 0))

    this.scene.add(mesh)

    // const material = new THREE.MeshBasicMaterial({ color: 0x000000 })
    // const sphere = new THREE.Line(new SphereGeometry(20), material)
    // sphere.position.set(0, 0, 2)
    // this.scene.add(sphere)

    this.sprite.scale.set(40, 40, 10)
    renderer.render(this.scene, camera)
    ctx.drawImage(renderer.domElement, 0, 0)

    // this.scene.remove(mesh, line)
    // this.scene.remove(sphere)
    // material.dispose()
    // mesh.dispose()
    // geometry.dispose()
    console.log('hihi')
  }
}
